package Test1;
/*
 * Array, length 실습 예
 */


public class Test3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//int형 array 선
		int[] ar1 = new int[5];
		
		//double형 array 선
		double[] ar2 = new double[7];
		
		//float형 array 선언 
		float[] ar3;
		ar3 = new float[9];
		
		System.out.println("Array ar1 Length :" + ar1.length);
		System.out.println("Array ar2 Length :" + ar2.length);
		System.out.println("Array ar3 Length :" + ar3.length);
	}

}
